import Item1 from "../../img/item1.jpg";
import Item2 from "../../img/item2.jpg";
import Item3 from "../../img/item3.jpg";
import Item4 from "../../img/item4.jpg";
import Item5 from "../../img/item5.jpg";
import Item6 from "../../img/item6.jpg";
import {
  ADD_TO_CART,
  ADD_QUANTITY,
  SUBTRACT_QUANTITY,
  REMOVE_ITEM
} from "../actions/action-types/cart-actions";
import { loadState, saveState } from '../localStorage'

const initState = {
  items: [
    {
      id: 1,
      title: "Yeezy 700 Wave",
      desc:
        "Lorem ipsum dolor sit amet consectetur adipisicing elit. Architecto eius quo nemo aliquam eligendi molestiae",
      price: 350,
      img: Item1
    },
    {
      id: 2,
      title: "Nike Airmax 270",
      desc:
        "Lorem ipsum dolor sit amet consectetur adipisicing elit. Architecto eius quo nemo aliquam eligendi molestiae",
      price: 165,
      img: Item2
    },
    {
      id: 3,
      title: "Puma RS x TOYS",
      desc:
        "Lorem ipsum dolor sit amet consectetur adipisicing elit. Architecto eius quo nemo aliquam eligendi molestiae",
      price: 120,
      img: Item3
    },
    {
      id: 4,
      title: "Adidas NMD CS2",
      desc:
        "Lorem ipsum dolor sit amet consectetur adipisicing elit. Architecto eius quo nemo aliquam eligendi molestiae",
      price: 95,
      img: Item4
    },
    {
      id: 5,
      title: "Air Jordan 1",
      desc:
        "Lorem ipsum dolor sit amet consectetur adipisicing elit. Architecto eius quo nemo aliquam eligendi molestiae",
      price: 160,
      img: Item5
    },
    {
      id: 6,
      title: "Nike Element 87",
      desc:
        "Lorem ipsum dolor sit amet consectetur adipisicing elit. Architecto eius quo nemo aliquam eligendi molestiae",
      price: 135,
      img: Item6
    }
  ],
  addedItems: [],
  totalQuantity: 0,
  total: 0
};

const cartReducer = (state = initState, action) => {

  switch (action.type) {
    case ADD_TO_CART:
      let addedItem = state.items.find(item => item.id === action.id);
     
      //console.log(addedItem);
      let existed_item = state.addedItems.find(item => action.id === item.id);
      let initQuantity = state.totalQuantity;

      //console.log(existed_item);
      if (existed_item) {
        addedItem.quantity += 1;
        initQuantity += 1;
        saveState({
          ...state,
          total: state.total + addedItem.price,
          totalQuantity: initQuantity
        })
        return {
          ...state,
          total: state.total + addedItem.price,
          totalQuantity: initQuantity
        };
      } else {
        addedItem.quantity = 1;
        initQuantity += 1;
        saveState({
          ...state,
          addedItems: [...state.addedItems, addedItem],
          total: state.total + addedItem.price,
          totalQuantity: initQuantity
        })
        return {
          ...state,
          addedItems: [...state.addedItems, addedItem],
          total: state.total + addedItem.price,
          totalQuantity: initQuantity
        };
      }

    case ADD_QUANTITY:
      let addItem = state.items.find(item => item.id === action.id);
      initQuantity = state.totalQuantity
      addItem.quantity += 1
      initQuantity += 1
      saveState({
        ...state,
        total: state.total + addItem.price,
        totalQuantity: initQuantity
      })

      return{
        ...state,
        total: state.total + addItem.price,
        totalQuantity: initQuantity
      }

    case SUBTRACT_QUANTITY:
      let subItem = state.items.find(item => item.id === action.id);
      initQuantity = state.totalQuantity
      subItem.quantity -= 1
      initQuantity -= 1
      if(subItem.quantity<=0){
        let selectedItem = state.items.find(item => item.id === action.id);
        let newItems  = state.addedItems.filter( item => !(item.id === action.id))
  
        saveState({
          ...state,
          addedItems: newItems,
          total: state.total - selectedItem.price,
          totalQuantity: initQuantity
        })
        
        return{
          ...state,
          addedItems: newItems,
          total: state.total - selectedItem.price,
          totalQuantity: initQuantity
        }
  
      } else{

        saveState({
          ...state,
          total: state.total - subItem.price,
          totalQuantity: initQuantity
        })
        return{
          ...state,
          total: state.total - subItem.price,
          totalQuantity: initQuantity
        }  
      }




    case REMOVE_ITEM:

      let selectedItem = state.items.find(item => item.id === action.id);
      let newItems  = state.addedItems.filter( item => !(item.id === action.id))
      initQuantity = state.totalQuantity - selectedItem.quantity

      return{
        ...state,
        addedItems: newItems,
        total: state.total - (selectedItem.price*selectedItem.quantity),
        totalQuantity: initQuantity
      }

    

    default:
      saveState(state);
      return loadState();
      
  }
};

export default cartReducer;
