import React, { Component } from 'react'
import { connect } from 'react-redux'

class Confirmation extends Component {

    componentDidMount = () => {
        console.log(this.props.details)
    }

    render() {

        const summaryCard = this.props.items.map(item => {
            return (
              <div
                className="card-content row"
                key={item.id}
                style={{ paddingTop: "0px", marginBottom: "0px" }}
              >
                <div className="col s8">
                  <p>
                    <b>
                      {item.quantity} x {item.title}
                    </b>
                  </p>
                  <p>{item.desc}</p>
                </div>
                <div className="col s4 right-align">
                  <p>
                    <b>{item.quantity}</b>
                  </p>
                </div>
              </div>
            );
          });
      

        const detailsInfo = (
            <div className="row">
                <div className="col s12 m6 l6">

                </div>
                <div className="col s12 m6 l6">
                    <p><b>Your order summary</b></p>
                    {summaryCard}
                </div>

            </div>
        )

        return (
            <div className="container">
                <h4 className="center" style={{ padding: "20px" }}>Order confirmation</h4>

            </div>
        )
    }
}

const mapStateToProps = (state) => {
    return{
        items: state.cart.addedItems,
        total: state.cart.total,
        details: state.checkout
    }
}

export default connect(mapStateToProps)(Confirmation)
