import React, { Component } from "react";
import { connect } from "react-redux";
import { addToCart } from '../actions/cartActions'
import { loadState, saveState } from '../localStorage'

class Home extends Component {

  handleFilter = (e, items) => {
    console.log(e.target.value)
    switch(e.target.value) {
      case 'low-high':
        console.log('low-high tab is selected')
        this.props.filterLowToHigh(items)
        break;

      case 'high-low':
        console.log('high-low tab is selected')
        break;

      default:
        console.log('recommended is selected')

    }
  }

    handleClick = (id) => {
        this.props.addToCard(id)
    }
  render() {
    //console.log(this.props);
    const itemList = this.props.items.map(item => {
      return (
        <div className="col s12 m6 l4" key={item.id} >
            <div className="card" style={{width: 'auto', height: 'auto'}}>
              <div className="card-image">
                <img src={item.img} alt={item.title} />
                <h6 className="white grey-text text-darken-3 card-content" ><b>{item.title}</b></h6>
                <a className="btn-floating halfway-fab waves-effect waves-light red darken-3" onClick={() => {this.handleClick(item.id)}}>
                  <i className="material-icons">add</i>
                </a>
              </div>

              <div className="card-content grey darken-3 white-text">
                <p>{item.desc}</p>
                <br />
                <p>
                  <b>Price: ${item.price}</b>
                </p>
              </div>
            </div>
            </div>
     ) });

    return (
      <div className="container">
        <h3 className="center">Product List</h3>
        <div className="row">
        <div className="input-field col s3 right">
          <select className="browser-default" onChange={() => this.handleFilter(this.props.items)}>
            <option value="" disabled>filter by</option>
            <option value="recommended">Recommended</option>
            <option value="low-high">Price: low to high</option>
            <option value="high-low">Price: high to low</option>
          </select>          
          </div>


        </div>


        <div className="row">
        {itemList}
        
          </div>
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    items: state.items
  };
};

const mapDispatchToProps = (dispatch) => {
    return{
        addToCard: (id) => {dispatch(addToCart(id))},
        filterLowToHigh: () => {dispatch(filterLowToHigh())}
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Home);
