import React, { Component } from 'react'
import { connect } from 'react-redux'

class Home extends Component {
    render() {
       console.log(this.props.items)
       const itemList = this.props.items.map( item => {
           return(
            <div class="card" key={item.id}>
                <span className="card-title black-text">{item.title}</span>
                <div className="card-image">
                
                    <img src={item.img} alt={item.title} />
                    
                    <a className="btn-floating halfway-fab waves-effect waves-light red"><i class="material-icons">add</i></a>
                </div>
                <div className="card-content grey darken-3 white-text">
                    <p>{item.desc}</p>
                    <p><b>Price: {item.price}$</b></p>
                </div>

                </div>
           )
       })

        return (
            <div className="container">
                <h3 className="center">Product List</h3>
                <div className="box">
                    {itemList}
                </div>
            </div>
        )
    }
}

const mapStateToProps = (state) => {
    return {
        items: state.items
    }
}

export default connect(mapStateToProps)(Home)
