import React, { Component } from "react";
import { connect } from "react-redux";

class Home extends Component {

    handleClick = (id) => {
        this.props.addToCard(id)
    }
  render() {
    console.log(this.props.items);
    const itemList = this.props.items.map(item => {
      return (
          <div className="col s4">
            <div class="card" key={item.id}>
              <div className="card-image">
                <img src={item.img} alt={item.title} />
                <h6 className="white grey-text text-darken-3" >{item.title}</h6>
                <a className="btn-floating halfway-fab waves-effect waves-light red" onClick={() => {this.handleClick(item.id)}}>
                  <i class="material-icons">add</i>
                </a>
              </div>

              <div className="card-content grey darken-3 white-text">
                <p>{item.desc}</p>
                <p>
                  <b>Price: {item.price}$</b>
                </p>
              </div>
            </div>
          </div>
      );
    });

    return (
      <div className="container">
        <h3 className="center">Product List</h3>
        <div className="row">{itemList}</div>
      </div>
    );
  }
}

const mapStateToProps = state => {
  return {
    items: state.items
  };
};

const mapDispatchToProps = (dispatch) => {
    return{
        ADD_TO_CART: (id) => {dispatch(addToCard(id))}
    }
}

export default connect(mapStateToProps)(Home);
